svg.script:
//
    var anim_running = true;
    // restart animation
    function reset_animation() {
      var el = document.querySelectorAll('[class*="kf-"]');
      for (var i = 0; i < el.length; i++) {
        el[i].style.animation = 'none';
        el[i].clientHeight; /* trigger reflow: https://gist.github.com/paulirish/5d52fb081b3570c81e3a */
        el[i].style.animation = '';

      }
    }
    function toggle_animation() {
      var el = document.querySelectorAll('[class*="kf-"]');
      if (anim_running){
        el[i].style.animationPlayState = 'running';
      }
    }
 //
